<?php

function nl_array($str)
{
	if(!$str)
		return [];

	$arr = explode("\n", $str);

	return $arr;
}

function array2nl($array)
{
	$nl = '';

	foreach($array as $ar)
	{
		$nl .= $ar . "\n";
	}

	return rtrim($nl, "\n");
}

function nl_list($str, $class='', $tag='div', $plus=null)
{
	$arr = $str;
	if(!is_array($str))
		$arr = nl_array($str);

	$out = '';
	foreach($arr as $r)
	{
		if($r)
			$out .= '<' . $tag . ' class="'.$class.'">' . ($plus ? $plus . ' ' : '') . $r . '</'. $tag .'>';
	}

	return $out;
}

function nl_array_first($str)
{
	if(is_array($str))
		return (count($str) > 0 ? $str[0] : $str);

	$str = nl_array($str);

	return $str[0];
}

function nl_array_end($str)
{
	$str = nl_array($str);

	return $str[count($str)-1];
}

