function link_nl2obj($str)
{
	$str = rtrim($str, "\n");
	$link_array = explode("\n", $str);

	$new_array = [];

	if(count($link_array) > 0)
	{
		foreach($link_array as $k => $link)
		{
			if(!empty($link))
			{		
				$new_array[$k]['id'] = str_random();
				$new_array[$k]['value'] = $link;
			}
		}
	}

	return $new_array;
}

